import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

const Dashboard = ({ token, onLogout }) => {
  const [evaluations, setEvaluations] = useState([]);
  const [cvs, setCvs] = useState([]);
  const [selectedCandidate, setSelectedCandidate] = useState(null);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  // Decode token to get role
  const role = token ? JSON.parse(atob(token.split('.')[1])).role : '';

  const fetchEvaluations = async () => {
    try {
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };
      const response = await axios.get('http://127.0.0.1:8000/api/evaluations', config);
      setEvaluations(response.data);
    } catch (error) {
      console.error('Error fetching evaluations:', error);
      setMessage(error.response?.data?.detail || 'Error fetching evaluations.');
    }
  };

  const fetchCvs = async () => {
    try {
      setIsLoading(true);
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };
      const response = await axios.get('http://127.0.0.1:8000/api/cvs', config);
      setCvs(response.data);
    } catch (error) {
      console.error('Error fetching CVs:', error);
      setMessage(error.response?.data?.detail || 'Error fetching CVs.');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchCvs();
    fetchEvaluations();
  }, [token]);

  const getCvDetails = (username, job_id) => {
    return cvs.find(cv => cv.username === username && cv.job_id === job_id) || {};
  };

  const getEvaluation = (username, job_id) => {
    return evaluations.find(evaluation => evaluation.username === username && evaluation.job_id === job_id) || null;
  };

  const handleEvaluateCv = async (username, job_id) => {
    try {
      if (!username) {
        setMessage('Cannot evaluate CV: Username is missing.');
        return;
      }
      if (job_id === null || job_id === undefined) {
        setMessage('Cannot evaluate CV: Job ID is missing.');
        return;
      }
      
      setIsLoading(true);
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };
      const response = await axios.post('http://127.0.0.1:8000/api/evaluate-cv', { username, job_id }, config);
      setMessage(response.data.message || 'CV evaluated successfully!');
      await fetchEvaluations();
    } catch (error) {
      console.error('Evaluation error:', error.response?.data);
      let errorMessage = 'Error evaluating CV.';
      if (error.response?.data?.detail) {
        if (typeof error.response.data.detail === 'string') {
          errorMessage = error.response.data.detail;
        } else {
          errorMessage = error.response.data.detail.msg || JSON.stringify(error.response.data.detail);
        }
      }
      setMessage(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  const openModal = (candidate) => {
    setSelectedCandidate(candidate);
  };

  const closeModal = () => {
    setSelectedCandidate(null);
  };

  const getSkills = (structuredContent) => {
    if (!structuredContent) return [];
    const cvSkills = structuredContent.skills || [];
    const cleanedSkills = [];
    for (const skillEntry of cvSkills) {
      if (["achievements", "positions of responsibility", "courses taken"].some(section => skillEntry.toLowerCase().includes(section))) {
        break;
      }
      if (skillEntry.includes("•")) {
        const skillPart = skillEntry.split("•").slice(-1)[0].trim();
        const skills = skillPart.replace(",", " ").split().map(s => s.trim().replace("*", ""));
        cleanedSkills.push(...skills);
      }
    }
    return cleanedSkills;
  };

  const handleLogout = () => {
    onLogout();
    navigate('/login');
  };

  // Function to determine score color
  const getScoreColor = (score) => {
    if (score >= 8) return '#4CAF50'; // Green
    if (score >= 5) return '#FFC107'; // Yellow
    return '#F44336'; // Red
  };

  return (
    <div className="dashboard-container">
      <header className="dashboard-header">
        <div className="header-content">
          <h1 className="dashboard-title">CvAlign Dashboard</h1>
          <p className="dashboard-subtitle">Candidate Evaluation System</p>
        </div>
        <button onClick={handleLogout} className="logout-button">
          <i className="fas fa-sign-out-alt"></i> Logout
        </button>
      </header>

      <nav className="dashboard-nav">
        <ul className="nav-list">
          <li className="nav-item">
            <Link to="/job-description" className="nav-link">
              <i className="fas fa-file-alt"></i> Job Description
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/dashboard" className="nav-link active">
              <i className="fas fa-tachometer-alt"></i> Dashboard
            </Link>
          </li>
          {role === 'admin' && (
            <li className="nav-item">
              <Link to="/job-openings" className="nav-link">
                <i className="fas fa-briefcase"></i> Job Openings
              </Link>
            </li>
          )}
        </ul>
      </nav>

      <main className="dashboard-content">
        {message && (
          <div className={`alert ${message.includes('success') ? 'alert-success' : 'alert-error'}`}>
            {message}
          </div>
        )}

        <div className="content-header">
          <h2 className="content-title">Candidate Applications</h2>
          <div className="stats-summary">
            <div className="stat-card">
              <span className="stat-number">{cvs.length}</span>
              <span className="stat-label">Total CVs</span>
            </div>
            <div className="stat-card">
              <span className="stat-number">
                {evaluations.length}
              </span>
              <span className="stat-label">Evaluated</span>
            </div>
          </div>
        </div>

        {isLoading ? (
          <div className="loading-container">
            <div className="spinner"></div>
            <p>Loading candidate data...</p>
          </div>
        ) : cvs.length === 0 ? (
          <div className="empty-state">
            <i className="fas fa-file-alt empty-icon"></i>
            <h3>No Applications Yet</h3>
            <p>No candidates have applied to your job description yet.</p>
          </div>
        ) : (
          <div className="table-container">
            <table className="candidates-table">
              <thead>
                <tr>
                  <th>Candidate</th>
                  <th>CV</th>
                  <th>Job Role</th>
                  <th>Score</th>
                  <th>Status</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {cvs.map((cv) => {
                  const evaluation = getEvaluation(cv.username, cv.job_id);
                  return (
                    <tr key={`${cv.username}-${cv.job_id}`}>
                      <td>
                        <div className="candidate-info">
                          <div className="avatar">
                            <i className="fas fa-user"></i>
                          </div>
                          <div>
                            <div className="candidate-name">{cv.username}</div>
                            <div className="candidate-id">ID: {cv.id}</div>
                          </div>
                        </div>
                      </td>
                      <td>
                        <a 
                          href={cv.cloud_url} 
                          target="_blank" 
                          rel="noopener noreferrer" 
                          className="cv-link"
                        >
                          <i className="fas fa-file-pdf"></i> {cv.filename}
                        </a>
                      </td>
                      <td>{cv.job_title || 'Not specified'}</td>
                      <td>
                        {evaluation ? (
                          <span 
                            className="score-badge" 
                            style={{ backgroundColor: getScoreColor(evaluation.relevance_score) }}
                          >
                            {evaluation.relevance_score.toFixed(1)}
                          </span>
                        ) : (
                          <span className="score-badge not-evaluated">N/A</span>
                        )}
                      </td>
                      <td>
                        {evaluation ? (
                          <span className="status-badge evaluated">
                            <i className="fas fa-check-circle"></i> Evaluated
                          </span>
                        ) : (
                          <span className="status-badge pending">
                            <i className="fas fa-clock"></i> Pending
                          </span>
                        )}
                      </td>
                      <td>
                        <div className="action-buttons">
                          {!evaluation ? (
                            <button
                              onClick={() => handleEvaluateCv(cv.username, cv.job_id)}
                              className="btn-evaluate"
                              disabled={isLoading}
                            >
                              <i className="fas fa-star"></i> Evaluate
                            </button>
                          ) : (
                            <button
                              onClick={() => openModal(evaluation)}
                              className="btn-details"
                            >
                              <i className="fas fa-eye"></i> Details
                            </button>
                          )}
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        )}
      </main>

      {selectedCandidate && (() => {
        const cv = getCvDetails(selectedCandidate.username, selectedCandidate.job_id);
        const structuredContent = cv.structured_content || {};
        const jobSkills = ["leadership", "agile"];
        const cvSkills = getSkills(structuredContent);
        const matchingSkills = cvSkills.filter(skill => jobSkills.includes(skill.toLowerCase()));
        const missingSkills = jobSkills.filter(skill => !cvSkills.some(cvSkill => cvSkill.toLowerCase() === skill.toLowerCase()));

        return (
          <div className="modal-backdrop">
            <div className="modal">
              <div className="modal-header">
                <h2>
                  <i className="fas fa-user-tie"></i> {selectedCandidate.username}
                </h2>
                <button onClick={closeModal} className="modal-close">
                  <i className="fas fa-times"></i>
                </button>
              </div>

              <div className="modal-body">
                <div className="modal-section">
                  <h3 className="section-title">
                    <i className="fas fa-chart-line"></i> Evaluation Summary
                  </h3>
                  <div className="summary-grid">
                    <div className="summary-card">
                      <div className="summary-score" style={{ color: getScoreColor(selectedCandidate.relevance_score) }}>
                        {selectedCandidate.relevance_score.toFixed(1)}
                      </div>
                      <div className="summary-label">Relevance Score</div>
                    </div>
                    <div className="summary-card">
                      <div className="summary-score">
                        {matchingSkills.length}/{jobSkills.length}
                      </div>
                      <div className="summary-label">Skills Match</div>
                    </div>
                  </div>
                  <div className="feedback-box">
                    <h4>Feedback:</h4>
                    <p>{selectedCandidate.feedback}</p>
                  </div>
                </div>

                <div className="modal-section">
                  <h3 className="section-title">
                    <i className="fas fa-file-alt"></i> CV Analysis
                  </h3>
                  
                  <div className="analysis-grid">
                    <div className="analysis-card">
                      <h4>Matching Skills</h4>
                      {matchingSkills.length > 0 ? (
                        <ul className="skill-list">
                          {matchingSkills.map((skill, index) => (
                            <li key={index} className="skill-match">
                              <i className="fas fa-check-circle"></i> {skill}
                            </li>
                          ))}
                        </ul>
                      ) : (
                        <p className="no-skills">No matching skills found</p>
                      )}
                    </div>
                    
                    <div className="analysis-card">
                      <h4>Missing Skills</h4>
                      {missingSkills.length > 0 ? (
                        <ul className="skill-list">
                          {missingSkills.map((skill, index) => (
                            <li key={index} className="skill-missing">
                              <i className="fas fa-times-circle"></i> {skill}
                            </li>
                          ))}
                        </ul>
                      ) : (
                        <p className="no-skills">All required skills found</p>
                      )}
                    </div>
                  </div>
                </div>

                <div className="modal-section">
                  <h3 className="section-title">
                    <i className="fas fa-graduation-cap"></i> CV Details
                  </h3>
                  
                  <div className="details-tabs">
                    <div className="tab">
                      <h4>Skills</h4>
                      <ul className="details-list">
                        {(structuredContent.skills || []).slice(0, 5).map((skill, index) => (
                          <li key={index}>
                            <i className="fas fa-check"></i> {skill}
                          </li>
                        ))}
                        {(structuredContent.skills || []).length > 5 && (
                          <li className="more-items">
                            +{(structuredContent.skills || []).length - 5} more skills
                          </li>
                        )}
                      </ul>
                    </div>
                    
                    <div className="tab">
                      <h4>Experience</h4>
                      <ul className="details-list">
                        {(structuredContent.experience || []).slice(0, 3).map((exp, index) => (
                          <li key={index}>
                            <i className="fas fa-briefcase"></i> {exp}
                          </li>
                        ))}
                        {(structuredContent.experience || []).length > 3 && (
                          <li className="more-items">
                            +{(structuredContent.experience || []).length - 3} more entries
                          </li>
                        )}
                      </ul>
                    </div>
                    
                    <div className="tab">
                      <h4>Education</h4>
                      <ul className="details-list">
                        {(structuredContent.education || []).map((edu, index) => (
                          <li key={index}>
                            <i className="fas fa-university"></i> {edu}
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </div>

                {cv.cloud_url && (
                  <div className="modal-actions">
                    <a 
                      href={cv.cloud_url} 
                      target="_blank" 
                      rel="noopener noreferrer" 
                      className="btn-view-cv"
                    >
                      <i className="fas fa-external-link-alt"></i> View Full CV
                    </a>
                  </div>
                )}
              </div>
            </div>
          </div>
        );
      })()}
    </div>
  );
};

export default Dashboard;
